import argparsedef sail():    ship_name = 'Von'    print(f'{ship_name} is wonderful')def list_ships():    ships = ['Von', 'von', '폰']    print(f"Ships : {', '.join(ships)}")def greet(greeting, name):    message = f'{greeting} {name}!'    print(message)if __name__ == '__main__':    parser = argparse.ArgumentParser(description='With Von')    parser.add_argument('--twice', '-t',                        help='Do it Twice',                        action='store_true') # 옵션 인수에 bool을 저장한다는 뜻.    subparsers = parser.add_subparsers(dest='func')    ship_parser = subparsers.add_parser('ships',                                        help='Ship related commands')    ship_parser.add_argument('command',                             choices=['list', 'sail'])    sailor_parser = subparsers.add_parser('sailors',                                          help='Talk to a sailor')    sailor_parser.add_argument('name',                               help='Sailors name')    sailor_parser.add_argument('--greeting', '-g',                               help='Greeting',                               default='Hello Von!!')    args = parser.parse_args()    if args.func == 'sailors':        greet(args.greeting, args)    elif args.command == 'list':        list_ships()    else:        sail()