import argparseif __name__ == '__main__':    parser = argparse.ArgumentParser(description='With Von')    parser.add_argument('message',                        help='Message to Von')    parser.add_argument('--twice', '-t',                        help='Do it Twice',                        action='store_true') # 옵션 인수에 bool을 저장한다는 뜻.    args = parser.parse_args()    print(args.message)    if args.twice:        print(args.message)