import click# 다른 그룹들과 명령이 위치할 최상위 그룹 만들기@click.group()def cli(): # 최상위 그룹 역할을 하는 함수 생성! click.group 메서드가 함수를 그룹으로 변환한다.    pass# ships 명령을 가질 그룹 만들기@click.group(help='Ship related commands')def ships():    pass# ships 그룹을 최상위 그룹의 명령으로 추가한다.cli.add_command(ships)# ships 그룹에 명령 추가# click.command 대신에 ships.command 명령이 사용되었다.@ships.command(help='Sail a ship')def sail():    ship_name = "Von's Ship"    print(f"{ship_name} is setting sail!")@ships.command(help='List all of the ships')def list_ships():    ships = ['Von', 'von', '폰']    print(f"Ships : {', '.join(ships)}")# cli 그룹에 명령 추가@cli.command(help='Talk to Von')@click.option('--greeting', default='Von is great!', help='Greeting for Von')@click.argument('name')def sailors(greeting, name):    message = f"{greeting} {name}"    print(message)if __name__ == '__main__':    # 최상위 그룹을 호출한다.    cli()