import fire# ship 명령을 위한 클래스 정의class Ships:    def sail(self):        ship_name = "Von's Ship"        print(f"{ship_name} is great!")    def list(self):        ships = ['Von', 'von', '폰']        print(f"Ships: {','.join(ships)}")# 하위 명령이 없으면 함수로 정의해도 된다!def sailors(greeting, name):    message = f"{greeting} {name}"    print(message)# 최상위 그룹 역할을 하는 클래스 정의# 클래스 속성으로는 sailor 함수와 Ships 클래스를 추가해준다.class Cli:    def __init__(self):        self.sailors = sailors        self.ships = Ships()if __name__ == '__main__':    # 최상위 그룹 역할을 수행하는 클래스를 호출한다.    fire.Fire(Cli)