import loggingimport osBASE_FORMAT = "[%(name)s] [%(levelname)-6s] %(message)s"FILE_FORMAT = "[%(asctime)s]" + BASE_FORMATroot_logger = logging.getLogger()root_logger.setLevel(logging.DEBUG)try:    file_logger = logging.FileHandler("application.log")except (OSError, IOError):    file_logger = logging.FileHandler("/tmp/application.log")    file_logger.setLevel(logging.INFO)    file_logger.setFormatter(logging.Formatter(BASE_FORMAT))    root_logger.addHandler(file_logger)