from flask import Response, Flaskimport prometheus_clientfrom prometheus_client import Counter, Histogramimport timeimport randomapp = Flask('prometheus-app')REQUEST = Counter(    'requests', 'Application Request Count',    ['endpoint'])TIMER = Histogram(    'slow', 'Slow Requests',    ['endpoint'])@app.route('/')def index():    REQUEST.labels(endpoint='/').inc()    return '<h1>Development Prometheus-backend Flask App</h1>'@app.route('/database/')def database():    with TIMER.labels('/database').time():        time.sleep(random.uniform(1, 3))    return '<h1>Completed expensive database operation</h1>'@app.route('/metrics/')def metrics():    return Response(        prometheus_client.generate_latest(),        mimetype='text/plain; version=0.0.4; charset=utf-8'    )if __name__ == '__main__':    app.run(host='127.0.0.1',            port=5000)